mapper.select:

select 

from DMS_DOCUMENT
where PARENT_ID = #{parentID, jdbcType=BIGINT} and DOC_NAME = #{docName,jdbcType=VARCHAR}

select DDT.ID, DDT.DOC_TYPE,
DDT.${order.property}
,
SUM ( B.LIST_VERSION_FLAG ) AS LIST_VERSION_FLAG, SUM ( B.INVITATION_FLAG ) AS INVITATION_FLAG, SUM ( B.UPDATE_FLAG ) AS UPDATE_FLAG, SUM ( B.CREATE_FOLDER_FLAG ) AS CREATE_FOLDER_FLAG, SUM ( B.CREATE_DOCUMENT_FLAG ) AS CREATE_DOCUMENT_FLAG, SUM ( B.SET_ALERT_FLAG ) AS SET_ALERT_FLAG, SUM ( B.RENAME_FLAG ) AS RENAME_FLAG, SUM ( B.DELETE_FLAG ) AS DELETE_FLAG, SUM ( B.COPY_FLAG ) AS COPY_FLAG, SUM ( B.MOVE_FLAG ) AS MOVE_FLAG, SUM ( B.ACCESS_CONTROL_FLAG ) AS ACCESS_CONTROL_FLAG, SUM ( B.AUDIT_TRAIL_FLAG ) AS AUDIT_TRAIL_FLAG, SUM ( B.DOWNLOAD_FLAG ) AS DOWNLOAD_FLAG, SUM ( B.EDIT_INDEX_FLAG ) AS EDIT_INDEX_FLAG FROM DMS_DOCUMENT DDT, DMS_HIERARCHY A, DMS_PERMISSION B WHERE DDT.PARENT_ID = #{parentID,jdbcType=BIGINT} AND DDT.ROOT_ID = #{rootID,jdbcType=BIGINT} AND DDT.RECORD_STATUS = 'A' AND DDT.HIERARCHY_ID = A.DOC_ID AND A.HIERARCHY_ID = B.DOC_ID AND ( EXISTS ( SELECT 1 FROM MTM_USER_ORGANIZE C WHERE B.ORG_ID= C.SOURCE_ORG_ID AND C.RELATE_ORG_ID= #{orgID,jdbcType=BIGINT} )) GROUP BY DDT.ID, DDT.DOC_TYPE
,
DDT.${order.property}
HAVING SUM(B.READ_FLAG)=0 ORDER BY DDT.DOC_TYPE DESC
,
DDT.${order.property} ${order.direction}
